int dis[N];
bool inque[N];
int f[N];
inline int read(void){
	int res,f=1;
	char c;
	while((c=getchar())<'0'||c>'9')
		if(c=='-')f=-1;
	res=c-48;
	while((c=getchar())>='0'&&c<='9')
		res=res*10+c-48;
	return res*f;		
}
void print(int x){
	if(x<0)
		putchar('-'),x=-x;
	if(x>9)
		print(x/10);
	putchar(x%10+'0');
	return;		
}
inline void add(int x,int y,int w){
	edge[++sum].to=y;
	edge[sum].next=head[x];
	edge[sum].v=w;
	head[x]=sum;
	return;
}
void dfs(int i,int fa){
	for(rr int j=head[i];j;j=edge[j].next){
		if(edge[j].to==fa)
			continue;
		dis[edge[j].to]=dis[i]^edge[j].v;
		dfs(edge[j].to,i);
	}
	return;
}
int find(int x){
	return f[x]==x?x:f[x]=find(f[x]);
}
inline void solve_tree(void){
