			f[find(Aedge[i].u)]=find(Aedge[i].v),add(Aedge[i].u,Aedge[i].v,Aedge[i].w),add(Aedge[i].v,Aedge[i].u,Aedge[i].w),++cnt;
		if(cnt==n-1)
			break;
