#include<cstdio>
#include<algorithm>
#include<queue>
#define Edge pair<int,int>
#define mp make_pair
using namespace std;
bool vis[100005];
int n,m,q,Xor[100005],Last[100005];
vector<Edge> G[100005];
void dfs(int now,int path)
{
	if(vis[now])	return ;
	vis[now]=true;
	Xor[now]=path;
	for(unsigned long i=0;i<G[now].size();++i)	dfs(G[now][i].second,path^G[now][i].first);
}//dfs求任意路径
int main(){
	scanf("%d %d %d",&n,&m,&q);
	for(int i=1;i<=m;++i)
	{
		int u,v,value;
		scanf("%d %d %d",&u,&v,&value);
		G[u].push_back(mp(value,v));
		G[v].push_back(mp(value,u));
	}
	dfs(1,0);
	while(q-->0)
	{
		int u,v;
		scanf("%d %d",&u,&v);
		printf("%d\n",Xor[u]^Xor[v]);//直接输出
	}
	return 0;
