#define maxn 100005
int n,m,q;
struct node
{
	int x,y,w;
}t[maxn*2];
inline bool cmp(node q,node p)
{
	return q.w<p.w;
}
int fa[maxn];
inline int find(int x)
{
	if(x==fa[x]) return x;
	return fa[x]=find(fa[x]);
}
struct edge
{
	int v,nxt,w;
}e[maxn*2];
int head[maxn*2],k=0;
inline void adde(int u,int v,int w)
{
	e[++k].v=v;
	e[k].w=w;
	e[k].nxt=head[u];
	head[u]=k;
}
int d[maxn];
inline void dfs(int u,int fa)
{

	for(int i=head[u];i;i=e[i].nxt)
	{
		int v=e[i].v;
		if(v==fa) continue ; 
		d[v]=d[u]^e[i].w;
		dfs(v,u);
	}
}
int main()
{
	n=read(),m=read(),q=read();
	for(int i=1;i<=m;i++)
	{
		t[i].x=read();
		t[i].y=read();
		t[i].w=read();
	}
	sort(t+1,t+m+1,cmp);
	for(int i=1;i<=n;i++)
		fa[i]=i;
	for(int i=1;i<=m;i++)
	{
		int u=t[i].x;
		int v=t[i].y;
		int fu=find(u);
		int fv=find(v);
		if(fu!=fv)
		{
			fa[fu]=fv;
			adde(u,v,t[i].w);
			adde(v,u,t[i].w);
		}
	}
	dfs(1,0);
	for(int i=1;i<=q;i++)
	{
		int a=read(),b=read();
		printf("%d\n",d[a]^d[b]); 
	}
	return 0;
}
