#include<bits/stdc++.h>
#define ll long long
#define re register
#define in inline
#define inf 1000000000 
using namespace std;
in int read(){
   int s=0,w=1;
   char ch=getchar();
   while(ch<'0'||ch>'9'){if(ch=='-')w=-1;ch=getchar();}
   while(ch>='0'&&ch<='9') s=s*10+ch-'0',ch=getchar();
   return s*w;
}
int n,m,q,tot=0;
int dis[400001],nx[400001];
bool vis[400001];
struct edge{int v,w,next;}e[400001];
in void addline(int u,int v,int w){
	e[++tot].v=v; e[tot].w=w;e[tot].next=nx[u]; nx[u]=tot;
}
in void bfs(int s){
	queue<int>q;
	memset(dis,0,sizeof(dis));
	memset(vis,0,sizeof(vis));
	q.push(s);
	while(!q.empty()){
		int u=q.front(); q.pop();
		if(vis[u]) continue;
		vis[u]=1;
		for(re int i=nx[u];i;i=e[i].next){
			int v=e[i].v,w=e[i].w;
			if(!vis[v]){q.push(v); dis[v]=dis[u]^w;}
		}

	}
}
int main(){
	n=read(); m=read(); q=read();
	for(re int i=1;i<=m;i++){
		int u=read(),v=read(),w=read();
		addline(u,v,w); addline(v,u,w);
	}
	bfs(1);
	for(re int i=1;i<=q;i++){
		int x=read(),y=read();
		int ans=dis[x]^dis[y];
		printf("%d\n",ans);
	}
	return 0;
}
