using namespace std;

const int maxn=1000006;

int a[maxn],cnt[maxn];

int n,m,q,type;

int query(int l,int r){
	if (l>r) swap(l,r);
	int pos=r;
	for(;;){
		pos-=cnt[pos];
		if (pos==l) return 0;
		if (pos<l) return 1;
		pos-=m+1;
	}
	return 1;
}

int A,B,C,P;
inline int rnd(){return A=(A*B+C)%P;}

