int n,m,q,type;

int x1,x2;

long long ans;

//int f[N][inf+1];

int g[N];

inline int rnd(){
	return A=(A*B+C)%P;
}

void dfs(int x,int fx){
	dfn[x]=++cnt;
	for(int i=head[x];i;i=nex[i]){
		int y=ver[i];
		if(y==fx) continue;
		dfs(y,x);
	}
	low[x]=cnt;
	return;
}

inline void Input(){
	n=read(),m=read(),q=read(),type=read();

	g[0]=n+1;

	for(re int i=1;i<=n;++i){
		a[i]=read();
		if(a[i]&1) g[i]=i;
		else g[i]=g[i-1];

		if(i>m)
		add(g[i-m-1],i);
		else add(n+1,i);
	}

	dfs(n+1,n+1);

	return;
}

inline void solve(){
	if(type)
	A=read(),B=read(),C=read(),P=read();
	for(re int i=1;i<=q;++i)
	{
		if(type) {x1=rnd()%n+1,x2=rnd()%n+1;}
		else	x1=read(),x2=read();
		if(x1>x2) swap(x1,x2);

		bool judge=0;

		if(a[x1]&1)
		{
			x2=g[x2];
			if(x2!=x1)
			if(!(dfn[x1]<=dfn[x2] && dfn[x2]<=low[x1])) judge=1;
		}
		else 
		judge=1;

		ans=(ans+1ll*judge*i*i)%mod;
	}
	printf("%lld\n",ans);
	return;
}

signed main(){
	Input();
	solve();
	return 0;
