```cpp
#include <iostream>
using namespace std;
typedef unsigned long long uLL;

string get(uLL len,uLL k){
    if(len==1){
        if(k==1)return "1";
        else return "0";
    }
    if(k>(1ull<<(len-1))-1){
        string tmp="1";
        uLL np=k-(1ull<<(len-1))+1;
        np=(1ull<<(len-1))-np;//于是并不会爆ull
        return tmp+get(len-1,np);
    }else{
        string tmp="0";
        return tmp+get(len-1,k);
    }
}

int main(){
    uLL n,k;
    cin>>n>>k;
    cout<<get(n,k)<<endl;
    return 0;
}
```

