#include <bits/stdc++.h>
#define ll unsigned long long
using namespace std;

string getb(int n,ll k){
	ll mid=1ull<<(n-1);
	if (n==0) return "";
	if (k<mid)
		return "0"+getb(n-1,k);
	else
		return "1"+getb(n-1,mid-1-(k-mid));
