```cpp
#include <iostream>
#include <cstdio>
#include <cstring>
using namespace std;
int a[1010],b[1010],t[1010];//a、t用来凑数，b存储题干的k 
char c[1010];
bool cmp() {//比较a与b 
	int w=1000;
	while(w>=0 && !a[w] && !b[w]) w--;
	while(w>=0 && a[w]==b[w]) w--;
	return a[w]>=b[w];
}
void mul(int x) {//高精乘 
	int w=1000;
	while(w>=0 && !a[w]) w--;
	for(int i=0;i<=w;i++) a[i]*=x;
	for(int i=0;i<=w;i++) {
		a[i+1]+=a[i]/10;
		a[i]%=10;
	}
	return;
}
void rev() {//对折 
	int w=1000;
	while(w>=0 && !a[w] && !b[w]) w--;
	if(cmp()) {
		for(int i=0;i<=w;i++) {
			if(a[i]<b[i]) {
				a[i+1]--;
				a[i]+=10;
			}
			t[i]=a[i]-b[i];
		}
	}
	else {
		for(int i=0;i<=w;i++) {
			if(b[i]<a[i]) {
				b[i+1]--;
				b[i]+=10;
			}
			t[i]=b[i]-a[i];
		}
	}
	memcpy(b,t,sizeof(t));
	return;
}
void code(int n) {//递归本体 
	if(n<=0) return;
	memset(a,0,sizeof(a));
	a[0]=1;
	for(int i=1;i<=n-1;i++) mul(2);//一点也不优雅的暴力乘方 
	a[0]--;//2的正整数次方结尾肯定是2、4、6、8，所以直接减就行 
	if(cmp()) {
		cout<<0;
		code(n-1);
	}
	else {
		cout<<1;
		mul(2);a[0]++;//一点也不优雅地凑出2^n-1 
		rev();//向左对折 
		code(n-1);
	}
}
int main() {
	int n;
	cin>>n;
	cin>>c;
	int w=1000;
	while(w>=0 && (c[w]>'9' || c[w]<'0')) w--;
	int i=0;
	while(w>=0 && c[w]>='0' && c[w]<='9') b[i++]=c[w--]-'0';
	code(n);
	return 0;
}
