	bool ans=k[n]^z;//预处理好每一位然后异或取值，手推得答案
	printf("%d",ans);
	dfs(n-1,k[n]);//继续向下分治
}

int main()
{
	cin>>n1>>k1;
	num=0;
	while(k1)//取每一位，避免爆ull
	{
		k[++num]=k1%2;
		k1/=2;
	}
	dfs(n1,0);//0表示不反转
}
