#include <stdio.h>

using namespace std;

template <typename T> inline void Read(T &t)
{
	int c=getchar(),f=0;
	for (;c<'0'||c>'9';c=getchar()) f=(c=='-');
	for (t=0;c>='0'&&c<='9';c=getchar()) t=(t<<3)+(t<<1)+(c^48);
	if (f) t=-t;
}

typedef long long ll;
const int N=5e5+5;

int n,tot,head[N],a[N],fa[N],re[N];
ll ans,cnt[N];
char temp[N];

struct Edge
{
	int to,next;
	void add(int x, int y) { to=y,next=head[x],head[x]=tot; }
} e[N<<1];

void dfs(int x, int f)
{
	cnt[x]+=cnt[f],fa[x]=f;
	if (a[x]==1)
	{
		while (a[f]!=-1&&re[f]!=-1) f=fa[re[f]];
		if (f==0||a[f]==1) re[x]=-1;
		else re[x]=f,cnt[x]+=cnt[fa[f]]-cnt[fa[fa[f]]]+1;
	}
	else re[x]=-1;
	for (int i=head[x];i;i=e[i].next)
	{
		int v=e[i].to;
		dfs(v,x);
	}
}	

signed main()
{
	Read(n);
	scanf("%s",temp);
	for (int i=1;i<=n;i++) 
		a[i]=(temp[i-1]=='('?-1:1);
	for (int i=2,f;i<=n;i++) Read(f)，e[++tot].add(f,i);

	re[0]=-1;
	dfs(1,0);

	for (ll i=1;i<=n;i++) ans^=(cnt[i]*i);
	printf("%lld\n",ans);

    return 0;
}
