## 以下是代码+注释

```cpp
#include <bits/stdc++.h>
using namespace std;
#define lng long long
const int N=5e5+10;
int n,fa[N];
struct brack{ //括号，f表示左右，d表示节点编号
	bool f;
	int d;
}bra[N];
char inp[N];
lng ans,dp[N],lst[N];
struct edge{ //树上加边
	int adj,nex;
}e[N];
int g[N],top;
void add(int x,int y){
	e[++top]=(edge){y,g[x]};
	g[x]=top;
}
stack<brack> s;
void dfs(int x){
	int tmp; //维护回溯
	if(s.empty()){ //RT
		tmp=x,s.push(bra[x]);
		dp[x]=dp[fa[x]];
		lst[x]=0;
	} else if(s.top().f==1&&bra[x].f==0){ //RT
		int fs=s.top().d;
		tmp=-fs,s.pop();
