const int MAXN = 5e5 + 3;

int n, head[MAXN], nxt[MAXN], to[MAXN], eid = 1;
void addedge(const int &u, const int &v){
	to[eid] = v;
	nxt[eid] = head[u];
	head[u] = eid++;
}
char s[MAXN];

int cnt[MAXN], a[MAXN<<1], b[MAXN<<1], *cntcnt = a + MAXN, *last = b + MAXN;
long long ans[MAXN];

list<int> q[MAXN];

void dfs(int i){
	if(s[i] == '(') cnt[i]++;
	else cnt[i]--;

	ans[i] += cntcnt[cnt[i]];
	cntcnt[cnt[i]]++;
	q[last[cnt[i] - 1]].push_back(i);
	int backup = last[cnt[i]];
	last[cnt[i]] = i;

	for(int e = head[i]; e; e = nxt[e]){
		cnt[to[e]] = cnt[i];
		dfs(to[e]);
	}

	last[cnt[i]] = backup;
	--cntcnt[cnt[i]];

	for(list<int>::iterator it = q[i].begin(); it != q[i].end(); it++)
		ans[*it] -= cntcnt[cnt[i] + 1];
}

void prefix(int i){
	for(int e = head[i]; e; e = nxt[e]){
		ans[to[e]] += ans[i];
		prefix(to[e]);
	}
}

