#include <iostream>
#include <vector>
#define rep(i, l, r) for (int i = (l); i <= (r); ++i)
using std::cin; using std::cout; using std::endl;
typedef long long ll;

const int N = 5e5+10;

int fa[N]; char c[N]; int n;
std::vector<int> g[N];
ll k[N], f[N];

int stk[N], top[N], siz[N], bak[N];

void dfs(int u){
    if (c[u] == '('){
        f[u] = 0;
        siz[u] = siz[ fa[u] ] + 1;
        top[u] = u;
        bak[u] = top[ fa[u] ];
        stk[u] = u;
    } else {
        if ( siz[ fa[u] ] > 0 ){
            f[u] = f[ fa[ stk[ top[fa[u]] ] ] ] + 1;
            siz[u] = siz[ fa[u] ] - 1;
            top[u] = bak[ top[fa[u]] ];
            bak[u] = bak[ top[u] ];
        } else {
            f[u] = 0;
        }
    }
    k[u] = k[ fa[u] ] + f[u];
    for(auto v : g[u]) dfs(v);
}

int main(){
    std::ios::sync_with_stdio(0); cin.tie(0); cout.tie(0);
    cin >> n >> (c+1);
