```
#include<bits/stdc++.h>
using namespace std;

const int N = 5e5 + 5;
int to[N], nxt[N], head[N], tot, cnt[N << 1], precnt[N << 1], a[N], n, x;
long long ans;
char s[N];

inline void addedge(int x, int y)
{
	to[++tot] = y;
	nxt[tot] = head[x];
	head[x] = tot;
}

void dfs(int x, long long res, int sum)
{
	int tt = res, tmp;
	sum += a[x];
	tmp = cnt[sum + N];
	if (~a[x]) cnt[sum + N] = precnt[sum + N]; else tt += precnt[sum + N] - cnt[sum + N];
	++precnt[sum + N];
	for (int i = head[x]; i; i = nxt[i])
	{
		int y = to[i];
		dfs(y, tt, sum);
	}
	ans ^= (long long)x * tt;
	cnt[sum + N] = tmp;
	--precnt[sum + N];
}

int main()
{
	scanf("%d%s", &n, s);
	for (int i = 0; i < n; i++)
		if (s[i] == '(') a[i + 1] = 1; else a[i + 1] = -1;
	for (int i = 2; i <= n; i++)
		scanf("%d", &x), addedge(x, i);
	++precnt[N];
	dfs(1, 0, 0);
	printf("%lld", ans);
}
```

