```cpp
/*
	Name: Transfer
	Copyright: No
	Author: ThaiMinda Simens
	Date: 16/11/19
	Description: CSP-J 2019 T2
*/
#include <iostream>
#include <cmath>
#include <cstring>
#include <bits/stdc++.h>
#include <cstdio>
using namespace std ;
long long n ;
long long g[100010] , p[100010] , t[100010] ;
long long sum = 0 ;
long long ques = 0 ;
int main(){
	freopen("transfer.in","r",stdin) ;
	freopen("transfer.out","w",stdout) ;
	queue<long long>q1 ;
	queue<long long>q2 ;
	cin >> n ;
	for ( int i = 1 ; i <= n ; i++ ){
		cin >> g[i] >> p[i] >> t[i] ;
		if ( g[i] == 0 ){
			sum += p[i] ;
			q1.push(t[i]) ;
			q2.push(p[i]) ;
			ques++ ;
		}
		while ( t[i] - q1.front() > 45 && !q1.empty() ){
			q1.pop() ;
			q2.pop() ;
			ques-- ;
		}
		long long f = 0 ;
		long long quesd = ques ;
		if ( g[i] == 1 ){
			if ( !q1.empty() ){
				for ( int j = 1 ; j <= quesd ; j++ ){
					if ( f == 0 && p[i] <= q2.front() ){
						f = 1 ;
						q1.pop() ;
						q2.pop() ;
						ques-- ;
					}
					else{
						q1.push(q1.front()) ;
						q2.push(q2.front()) ;
						q1.pop() ;
						q2.pop() ;
					}
				}
			}
			else{
				sum += p[i] ;
				continue ;
			}
			if ( f == 0 ){
				sum += p[i] ;
			}
		}
	}
	cout << sum ;
	return 0 ;
}
```

