#include<bits/stdc++.h>//万能头文件(guide能用)
using namespace std;
#define ull unsigned long long 
unsigned long long erase(ull a, ull b[], ull c, ull d)
{
	for (unsigned long long i = c; i < d; i++)
		if (a - b[i] < 46)//找到第一张未过时的
			return i;
	return d;
}
int use(ull a, ull b[], ull c, ull d)
{
	for (int i = c; i < d; i++)
		if (b[i] >= a)
			return i;
	return -1;
}
int main()
{
	freopen("transfer.in","r",stdin);
	freopen("transfer.out","w",stdout);
        ull a = 0, s = 0, put = 0, start = 0, end = 0;
        //start存储未过时第一张地铁票，end存储最后一张地铁票+1
        //s存储和,put表示优惠了多少
	int b = 0;//配合use使用
	cin >> a;
	ull f[a] = {0};//存储地铁优惠票价格
	ull h[a] = {0};//存储地铁优惠票时间
	ull g[a][3];//存储数据
	for (int i = 0; i < a; i++)
	{
		cin >> g[i][0];//类型：0地铁，1公交
		cin >> g[i][1];//价格
		cin >> g[i][2];//开始时间
		s += g[i][1];
		if (g[i][0] == 0)
		{
			f[end] = g[i][1];
			h[end] = g[i][2];
			end++;
		}//增加新的地铁优惠票
		start = erase(g[i][2], h, start, end);
        //删除所有过时地铁票
		if(g[i][0]==1)
		{
            b = use(g[i][1],f,start,end);
			if (b > -1)
			{
				put += g[i][1];
				f[b] = 0;
				h[b] = 0;
			}
		}//使用优惠票
	}
	cout << s - put;//求答案
	return 0;
}
