代码：
```cpp
//deque
#include<bits/stdc++.h>
using namespace std;
struct node{
	long long pri,price,time;          //结构体存储，price表示money，pri表示地铁和公交，time...不说了 
}a[100001];
deque<node>p,q;   //p是临时存储，q是总的票 
long long n,ans;
int main(){
	cin>>n;
	for(int i=1;i<=n;i++)
		scanf("%lld%lld%lld",&a[i].pri,&a[i].price,&a[i].time);
	//截止这里都是准备 
	for(int i=1;i<=n;i++){
		while(!q.empty()&&a[i].time-q.front().time>45) q.pop_front();   //处理过期作废的票 
		if(a[i].pri==0){        //地铁必须付钱 
			ans+=a[i].price;
			q.push_back(a[i]);
		}
		else if(a[i].pri==1){
			bool flag=false;
			while(!q.empty()){     //循环遍历q 
				node d=q.front();
				if(d.price>=a[i].price){
					flag=true;
					q.pop_front();
					break;            //符合的用掉，break 
				}
				p.push_back(q.front());
				q.pop_front();        //为下一个做准备，把当前队头弹掉 
			}
			if(!flag) ans+=a[i].price;   //不得不付 
			while(!p.empty()){            //弹回去 
				q.push_front(p.back());
				p.pop_back();
			}
		}
	}
	cout<<ans;             //输出，完美结束 
	return 0;
}
```
