int main(){
	cin >> n;
	for (int i=1;i<=n;i++){
		cin >> flag >> money >> Time;
		if (flag==0){
			Data[dataNum].cost=money;
			Data[dataNum].t=Time;
			dataNum++;
			ans+=money;
		}
		else{
			while(Time-Data[head].t>45&&head<dataNum){
				head++;
			}
			if (head>=dataNum){
				ans+=money;
				continue;
			}
			int temp=head;
			while((Data[temp].cost<money||check[temp])&&temp<dataNum){
				temp++;
			}
			if (temp>=dataNum){
				ans+=money;
				continue;
			}else{
				check[temp]=true;
			}
		}
	}
	cout << ans;
	return 0;
}
