用结构体"jl"（记录）记录每一条乘坐记录。用队列（q）存储能用的票，再进行判断。
```cpp
#include<bits/stdc++.h>//可替换
using namespace std;
struct jl{//记录
	long long w;//乘坐公交车还是地铁
	long long t;//时间
	long long p;//价格
}a[100005];
int main(){

queue<jl>q;//优惠券存储
int n,c=0;
cin>>n;
for(int i = 0;i<n;i++){
	cin>>a[i].w>>a[i].p>>a[i].t;//输入
	if(a[i].w==0){//如果是地铁，直接付钱
		q.push(a[i]);//把地铁票加入队列
		c+=a[i].p;//付钱
		continue;
	}
	int y=q.size();
	for(int j = 0;j<y;j++){//弹出过期的票
		if(a[i].t-q.front().t>45){
			q.pop();
		}else{
			q.push(q.front());
			q.pop();
		}
	}

	y=q.size();
	int cnt=1e9,pd=0;
	int cntj;
	for(int j = 0;j<y;j++){
		if(q.front().p>=a[i].p&&q.front().t<cnt){//找到当前最优的能用的票
			cnt=q.front().t;//记录这张票
			cntj=j;
			pd=1;//记录这样的票是存在的
		}
		q.push(q.front());//下一项
		q.pop();
	}
	if(pd==0){//如果没有
		c+=a[i].p;//付钱
		continue;
	}
	for(int j = 0;j<cntj;j++){//找到那张票
		q.push(q.front());
		q.pop();
	}
	q.pop();//用掉它
}
cout<<c;
	return 0;
}
