		fs = read();//fx表示乘坐方式~~我英文不好只会用汉语拼音~~
		p = read();
		t = read();//读入 
		while(head <= tail && (a[head].t + 45 < t || used[head]))//因为输入时间是单调上升的，所以只要比t小就就永远过期没用了 
			head++;
		ans += p;//把钱加上去 
