```java
import java.util.*;
import java.io.*;

class Main {

  static final int INF = (int) 1e3 + 7;

  public static void main(String[] args) {
    Scanner in = new Scanner(new BufferedReader(new InputStreamReader(System.in)));
    int t = in.nextInt();
    int n = in.nextInt();
    int m = in.nextInt();

    int[][] p = new int[t][n];
    for (int i = 0; i < t; i++) {
      for (int j = 0; j < n; j++) {
        p[i][j] = in.nextInt();
      }
    }

    for (int i = 0; i < t - 1; i++) {
      int[] w = p[i];
      int[] v = new int[n];
      for (int j = 0; j < n; j++) {
        v[j] = p[i + 1][j] - p[i][j];
      }
      m = knapsack(w, v, m);
    }
    System.out.println(m);
  }

  static int knapsack(int[] w, int[] v, int c) {
    int[] dp = new int[c + 1];
    int n = w.length;
    for (int i = 0; i < n; i++) {
      for (int j = w[i]; j <= c; j++) {
        dp[j] = Math.max(dp[j], dp[j - w[i]] + v[i]);
      }
    }
    return c + dp[c];
  }
}
