接下来讲84分做法（考场上无限接近）

我们再做出一个大的跨度：利用正难则反的思想。我们先把它所有做菜的可能性算出来：每一行的和+1乘起来。接着我们考虑gg的情况。首先很显然只会有1个菜gg。 

我们可以枚举那个gg的菜。接下来再dp。  _fijk_ 表示前i行 gg的菜选了j个，其它的菜选了k个，随便dp就行。代码：

```
#include<bits/stdc++.h>
using namespace std;

const int N = 41;
#define ll long long
#define FOR(i,a,b) for(int i = a; i <= b; ++i)
#define _FOR(i,a,b) for(int i = a; i >= b; --i)

template<typename T>void read(T &x)
{
	x = 0;int f = 1;
	char c = getchar();
	for(;!isdigit(c);c = getchar()) if(c == '-') f = -1;
	for(;isdigit(c);c = getchar()) x = x * 10 + c - '0';
	x *= f;
}

const ll mod = 998244353;
int n,m;
ll a[N][510],cnt[N],ans = 1,summ[N];
ll f[N][N][N];

void dfs(int x,ll num,int now)
{
	if(x == n + 1)
	{
		if(now != 0 && now / 2 >= max(cnt[1],max(cnt[2],cnt[3])) )	
			ans += num,ans %= mod;
		return ;
	}
	dfs(x + 1,num,now);
	FOR(i,1,m)
		cnt[i]++,dfs(x + 1,(a[x][i] * num) % mod,now + 1),cnt[i]--;
}

int main()
{
	//freopen(".in","r",stdin);
	//freopen(".out","w",stdout);
	read(n),read(m);
	FOR(i,1,n)
		FOR(j,1,m) read(a[i][j]);
	FOR(i,1,n)
	{
		ll sum = 1;
		FOR(j,1,m)
			sum += a[i][j];
		ans = ans * sum % mod;
	}
	//printf("%d\n",ans);
	FOR(gg,1,m)
	{
		memset(summ,0,sizeof(summ));
		FOR(i,1,n)
			FOR(j,1,m)
				if(j != gg) summ[i] += a[i][j],summ[i] %= mod;
		memset(f,0,sizeof(f));
		f[0][0][0] = 1;
		ll tmp1,tmp2,tmp3,tmp4;
		tmp1 = tmp2 = tmp3 = tmp4 = 0;
		FOR(i,1,n)
			FOR(j,0,n)
				FOR(k,0,n)
				{
					tmp1 = tmp2 = tmp3 = 0;
					if(j + k > i) break;
					tmp1 = f[i - 1][j][k];
					if(j != 0) tmp2 = f[i - 1][j - 1][k] * a[i][gg] % mod;
					if(k != 0) tmp3 = f[i - 1][j][k - 1] * summ[i] % mod;
					f[i][j][k] = tmp1 + tmp2 + tmp3,f[i][j][k] %= mod;
				}
		ll gjmm = 0;
		FOR(i,0,n)
			FOR(j,0,n)
			{
				int total = i + j;
				if(total > n)continue;
				if(i <= total / 2) continue;
				gjmm += f[n][i][j],gjmm %= mod;
			}
		//printf("QAQ%d\n",gjmm);
		ans = (ans - gjmm + mod) % mod;
	}
	printf("%lld\n",(ans - 1 + mod) % mod);
	return 0;
}
