	long long l, r;
}b[500005];

struct st3 {
	long long l, r, id;
}c[500005];
bool cmp1 (st1 x, st1 y) {
	return x.x < y.x;
}
bool cmp2 (st2 x, st2 y) {
	return x.l < y.l;
}
bool cmp3 (st3 x, st3 y) {
	return x.l < y.l;
}
void get_in (long long x, long long y) {
	b[++tot].l = min (x, y), b[tot].r = max (x, y);
}
long long n, m;
long long bit[500005];
long long lowbit (long long x) {
	return x & (-x);
}
long long sum (long long x) {
	long long ans = 0;
	for (; x; x -= lowbit (x)) ans += bit[x];
	return ans;
}
void add (long long x, long long y) {
	for (; x <= n; x += lowbit (x)) bit[x] += y;
}
long long ans[500005];
int main () {
	scanf ("%lld %lld", &n, &m);
	for (long long i = 1; i <= n; i++) {
		scanf ("%lld", &a[i].x);
		a[i].id = i;
	}
	sort (a + 1, a + 1 + n, cmp1);
	get_in (a[1].id, a[2].id);
	get_in (a[n-1].id, a[n].id);
	for (long long i = 2; i < n; i++) {
		if (a[i].x - a[i-1].x <= a[i+1].x - a[i].x) get_in (a[i].id, a[i-1].id);
		if (a[i].x - a[i-1].x >= a[i+1].x - a[i].x) get_in (a[i].id, a[i+1].id);
	}//统计配对
	for (long long i = 1; i <= m; i++) {
		scanf ("%lld %lld", &c[i].l, &c[i].r);
		c[i].id = i;
	}
	sort (b + 1, b + 1 + tot, cmp2);
	sort (c + 1, c + 1 + m, cmp3);//排序
	for (long long i = m; i >= 1; i--) {
		while (b[tot].l >= c[i].l) {
			add (b[tot].r, 1);
			tot--;
		}
		ans[c[i].id] = sum (c[i].r);
	}
	long long cnt = 0;
	for (long long i = 1; i <= m; i++) cnt += (ans[i] * i);
	printf ("%lld", cnt);
	return 0;
}
```

### 一篇树状数组题解
------------
题意：给出一个序列 $a$，多组询问求区间 $[l,r]$ 内的好的配对数，$(x,y)$ 是好的配对等价于在将序列所有元素在数轴上标记后，$a_y$ 是数轴上与 $a_x$ 最接近的元素之一，且 $(x,y)$ 配对有序。

观察数据范围发现需要离散序列 $a$，序列中不存在相同元素为解题提供了一些便利：不需去重，可以使用 ```unordered_map<int,int>``` 标记元素的位置。

另一方面可以发现 **$(x,y)$ 是好的配对不代表 $(y,x)$ 也是好的配对，如对于序列 ```1 3 4```，$(1,2)$ 为好的配对，但 $(2,1)$ 不是**。

将好的配对 $(x,y)$ 的贡献统计在树状数组中位置 $\min(x,y)$，对询问按照右端点排序，对于询问 $[l,r]$，答案为已经统计的好的配对数量减去位置 $l$ 以前的好的配对数量。

每个数至多被加入一次，加入操作和统计操作的复杂度同为单次 $O(\log n)$，总体复杂度 $O(n\log n)$。

实现略有难度，此为笔者第一次写出的[代码](https://www.luogu.com.cn/paste/tu5rcg3f)，可简化的部分极多。remake 后决定发题解记录，通过设置极值可以省略对 $n=1$ 等情况的特判。

```cpp
#include<bits/stdc++.h>
using namespace std;
constexpr int inf=1050000000;
struct Question{int l,r,i;}u[300001];
int n,m,pos,a[300002],c[300002],p[300002];
unordered_map<int,int> s;//s[x]:数x在序列a中的下标
long long ans,all;
bool on[300002];//on[i]:p[i]是否加入序列
bool cmp(const Question &x,const Question &y){return x.r<y.r;}
inline bool check(int x,bool y){return y ? p[x]-p[x-1]<=p[x+1]-p[x]:p[x]-p[x-1]>=p[x+1]-p[x];}
void add(int x){while(x<=n) c[x]++,x+=x&-x;}
int sum(int x){int ret=0;while(x) ret+=c[x],x-=x&-x;return ret;}
inline void update(int x,int y){all++,add(min(s[p[x]],s[p[y]]));}
void deal(int x){
	int it=lower_bound(p+1,p+n+1,x)-p;
	on[it]=1;//使用check判定好对是否成立,使用update更新数据
	if(on[it-1]&&check(it-1,0)) update(it,it-1);
	if(on[it+1]&&check(it+1,1)) update(it,it+1);
	if(on[it-1]&&check(it,1)) update(it,it-1);
	if(on[it+1]&&check(it,0)) update(it,it+1);
}
int main(){
	scanf("%d%d",&n,&m);
	for(int i=1;i<=n;i++) scanf("%d",&a[i]),p[i]=a[i],s[a[i]]=i;
	for(int i=1;i<=m;i++) scanf("%d%d",&u[i].l,&u[i].r),u[i].i=i;
	sort(p+1,p+n+1),sort(u+1,u+m+1,cmp);
	p[0]=-inf,p[n+1]=inf<<1;//左右赋极值
	for(int i=1;i<=m;i++){
		while(pos<u[i].r) deal(a[++pos]);
		ans+=(all-sum(u[i].l-1))*u[i].i;
	}
	printf("%lld\n",ans);
	return 0;
}
```
总结：本题的重点不在树状数组，而是如何统计好对。对于类似可能需要特判两端或极值的问题，设置极值是一个有效的策略，可以节省码量和思维难度。

### 题意简述


------------

给定一个数列，求一段区间内好配对的个数。好配对定义为若对于所有的 $i=1,2,\cdots,n$，满足 $|a_x-a_y|\le|a_x-a_i|(i\not=x)$，则称 $(x,y)$ 为一组好的配对（$|x|$ 表示 $x$ 的绝对值）。

### 做法


------------

- 对于一个数能与它形成好配对的一定是和它差值最小的数。顺着这个思路我们可以考虑将数列排序，排序后对于一个数字能与其匹配的只有它左边或右边的数，对于 $1$ 和 $n$ 的情况进行特判，如果两边差值相等就都是好配对。

- 现在问题就转化为给定一些配对找在 $l$ ，$r$ 这段区间中有几个好配对，对于这个问题，由于题目并未强制在线，我们可以考虑将询问离线下来。此时我们可以考虑把它转化为二维数点问题，对于每个配对用对应下标的最小值和最大值作为一个点，将每个询问拆成四个询问，$cdq$ 分治解决即可。

### 代码


------------

```cpp
#include<bits/stdc++.h>
#define F(i,x,n) for(int i=x;i<=n;++i)
#define _F(i,x,n) for(int i=x;i>=n;--i)
#define int long long
using namespace std;

inline int read(){
	int s=0;char c=getchar();bool flag=0;
	while(!isdigit(c))flag|=(c=='-'),c=getchar();
	while(isdigit(c))s=(s<<3)+(s<<1)+(c^48),c=getchar();
	return flag?-s:s; 
}

const int N=4e5+5;

struct node{
	int op,l,r,w,id;
	bool operator < (const node&p)const{
		return l==p.l?op<p.op:l<p.l;
	}
}q[N<<2],tem[N<<2];

struct node2{
	int v,id;
	bool operator < (const node2&p)const{
		return v<p.v;
	}
}a[N];

int c[N],res[N];
int n,m,tot;

void cdq(int l,int r){
	if(l==r)return;
	int mid=l+r>>1;
	cdq(l,mid),cdq(mid+1,r);
	int i=l,j=mid+1,o=l,cnt=0;
	while(i<=mid&&j<=r){
		if(q[i].r<=q[j].r){//先修改后询问
			if(!q[i].op)cnt++;
			tem[o++]=q[i++];
		}
		else{
			if(q[j].op)res[q[j].id]+=q[j].w*cnt;
			tem[o++]=q[j++];
		}
	}
	while(i<=mid)tem[o++]=q[i++];
	while(j<=r){
		if(q[j].op)res[q[j].id]+=q[j].w*cnt;
		tem[o++]=q[j++];
	}
	F(k,l,r)q[k]=tem[k];
}

signed main(){
	n=read(),m=read();
	F(i,1,n)a[i].v=read(),a[i].id=i;
	sort(a+1,a+1+n);
	F(i,1,n){//寻找好配对，注意对于每个点x要小于y插入。 
		if(i==1)q[++tot]=node{0,min(a[i].id,a[i+1].id),max(a[i].id,a[i+1].id),0,0};//特判i==1的情况
		else if(i==n)q[++tot]=node{0,min(a[i].id,a[i-1].id),max(a[i].id,a[i-1].id),0,0};//特判i==2的情况
		else{
			if(a[i].v-a[i-1].v>a[i+1].v-a[i].v)q[++tot]=node{0,min(a[i].id,a[i+1].id),max(a[i].id,a[i+1].id),0,0};
			else if(a[i].v-a[i-1].v<a[i+1].v-a[i].v)q[++tot]=node{0,min(a[i].id,a[i-1].id),max(a[i].id,a[i-1].id),0,0};
			else q[++tot]=node{0,min(a[i].id,a[i-1].id),max(a[i].id,a[i-1].id),0,0},q[++tot]=node{0,min(a[i].id,a[i+1].id),max(a[i].id,a[i+1].id),0,0};
		}
	}
	F(i,1,m){//将询问拆成4个询问计算
		int l=read(),r=read();
		q[++tot]=node{1,r,r,1,i};
		q[++tot]=node{1,l-1,l-1,1,i};
		q[++tot]=node{1,l-1,r,-1,i};
		q[++tot]=node{1,r,l-1,-1,i};
	}
	sort(q+1,q+1+tot);
	cdq(1,tot);
	int ans=0;
	F(i,1,m)ans+=res[i]*i;//统计答案
	cout<<ans<<'\n';
	return 0;
}
```

# 题意：
给你一个长为 $n$ 的序列 $a$。

$m$ 次询问，每次给出 $l$ 和 $r$。

求满足对所有 $i$ 满足 $|a_x - a_y| \le |a_x - a_i|$ 的二元组 $(x,y)$ 的对数。

# 思路：
考虑绝对值几何意义：以 $a_i$ 作为点 $i$ 对应的数，建立数轴。

那关系式用通俗的话来说就是：$x$ 与距离 $x$ 最近的点 $y$ 组成二元组 $(x,y)$。

而距离 $x$ 最近有两种情况：比 $a_x$ 小的最大的 $a_y$ 对应的 $y$ 与比 $a_x$ 大的最小的 $a_y$ 对应的 $y$。

这个东西有很多求法，最简单的是排个序，然后它的前一个和后一个位置就是这两个值了。然后简单的分类讨论就可以统计出所有的 $(x,y)$。

为了方便计算，我们钦定 $x < y$（如果不是就 swap 一下）。

如何统计答案呢？

考虑到不满足询问的好的配对只有两种情况：$x < l$ 或者 $r < y$。

我们先对询问按 $l$ 排序。

这样就满足如果在前一个询问中有 $x < l$ 那在这一个询问中也一定有 $x < l$。在考虑第二个条件，即 $y <= r$。我们处理出满足第一个条件好的配对的的集合，然后就有很多种方法处理了。

因为第一个条件满足单调性，所以集合只减不增，很好处理。

这里介绍最简单处理的方法：以配对的 $y$ 作为下标建立树状数组，每次找到不符合第一个条件的配对将其删去，然后查询 $r$ 的前缀和即是答案。

# 代码:

代码其实很短，不到 100 行。

```cpp
#include <bits/stdc++.h>

using namespace std; 

const int Maxn = 3e5 + 10; 

int n, m; 

int a[Maxn]; 

struct node {
	int val, id; 
} t[Maxn];

bool cmp(node x, node y) {
	return x.val < y.val; 
}

int p[Maxn][2]; 

vector<pair<int, int> > v; 

struct question {
	int l, r, id; 
	friend bool operator < (question x, question y) {
		return x.l < y.l; 
	}
} que[Maxn];

int tree[Maxn]; 

void add(int x, int y) {
	for (; x <= n; x += (x & -x)) tree[x] += y; 
}

int sum(int x) {
	int res = 0; 
	for (; x >= 1; x -= (x & -x)) res += tree[x]; 
	return res; 
}

long long ans; 

int main() {
	int i, j; 
	scanf("%d %d", &n, &m); 
	for (i = 1; i <= n; ++i) {
		scanf("%d", a + i); 
		t[i].val = a[i], t[i].id = i; 
	}
	sort(t + 1, t + n + 1, cmp); 
	for (i = 1; i <= n; ++i) {
		p[t[i].id][0] = t[i - 1].id; 
		p[t[i].id][1] = t[i + 1].id; 
	}
	for (i = 1; i <= n; ++i) {
		if ((!p[i][0]) && (!p[i][1])) continue; 
		if ((!p[i][0]) || (!p[i][1])) {
			if (!p[i][0]) v.push_back(make_pair(p[i][1], i)); 
			else if (!p[i][1]) v.push_back(make_pair(p[i][0], i)); 
			continue; 
		}
		else {
			if (abs(a[i] - a[p[i][0]]) == abs(a[i] - a[p[i][1]])) {
				 v.push_back(make_pair(p[i][0], i)); 
				 v.push_back(make_pair(p[i][1], i)); 
			} 
			else if (abs(a[i] - a[p[i][0]]) < abs(a[i] - a[p[i][1]])) v.push_back(make_pair(p[i][0], i)); 
			else if (abs(a[i] - a[p[i][0]]) > abs(a[i] - a[p[i][1]])) v.push_back(make_pair(p[i][1], i)); 
		}
	}
	for (i = 0; i < v.size(); ++i) {
		if (v[i].first > v[i].second) swap(v[i].first, v[i].second); 
	}
	sort(v.begin(), v.end());  
	for (i = 1; i <= m; ++i) {
		scanf("%d %d", &que[i].l, &que[i].r); 
		que[i].id = i; 
	}
	sort(que + 1, que + m + 1);
	for (i = 0; i < v.size(); ++i) {
		add(v[i].second, 1); 
	}
	j = 0; 
	for (i = 1; i <= m; ++i) {
		while (j < v.size() && v[j].first < que[i].l) add(v[j].second, -1), ++j; 
		ans += 1ll * sum(que[i].r) * que[i].id; 
	}
	printf("%lld\n", ans); 
	return 0; 
} 

```

$(x,y)$ 是一组配对的前提是 $(a_x,a_y)$ 在值域上是相邻的。于是我们可以先把 $a$ 数组排序，找出所有可能的配对 $(x,y)$。如果把每个配对看做二维平面上的点，那么查询 $[l,r]$ 就等价于查询横坐标和纵坐标均介于 $[l,r]$ 的点的数量，这是经典的二维数点问题。使用扫描线即可轻松解决。注意特判 $n=1$ 的情况。

（附赠另一道经典例题：[HH 的项链](https://www.luogu.com.cn/problem/P1972)）

代码如下：
```cpp
#include<iostream>
#include<cstdio>
#include<algorithm>
#include<cstring>
#include<cmath>
#include<vector>
#include<queue>
#include<map>
#define pii pair<int,int> 
