		return 0;
	}
	for(i=1;i<=cnt;i++)
	{
		for(j=0;j<ans1[i].p.size()-1;j++)
		{
			Q1[i][j].x=ans1[i].p[j];
			Q1[i][j].y=ans1[i].p[j+1];
