	int v,nxt;
}ed[MAXN<<2];
int head[MAXN],cnt;
inline void adde(int u,int v){
	ed[++cnt]=(Edge){v,head[u]};
	head[u]=cnt;
}
inline void add(int u,int v){
	adde(u,v);adde(v,u);
}

int dist[3][MAXN],id[3],vis[MAXN];

