queue <int> q; 
inline void bfs(int s) { // BFS 求最短路 
	memset(vis, 0, sizeof vis);
	memset(dis, inf, sizeof dis);
	dis[s] = 0; 
	q.push(s); 
	while(!q.empty()) {
		int u = q.front(); q.pop();
		if(vis[u]) continue;
		vis[u] = 1;
		for(int i = head[u]; i; i = nxt[i]) {
			int v = ver[i];
			if(vis[v]) continue;
			if(dis[v] > dis[u] + 1) {
				dis[v] = dis[u] + 1;
				q.push(v);	
			}
		}
	}
}
int main () {
	read(n); read(m);
	rep(i, 1, m) {
		int u, v;
		read(u); read(v);
		add(u, v);
	}
	int s1, s2, t1, t2;
	read(s1); read(t1); read(s2); read(t2);
	bfs(s1);
	rep(i, 1, n) dis1[i] = dis[i];
	bfs(s2);
