代码实现的时候，不用分类讨论，可以直接在 $1$ 到 $n$ 内 枚举公共点。

求最短路可以直接 $bfs$。

$Code$

```cpp
#include <iostream>
#include <cstdio>
#include <queue>
#include <cstring>
using namespace std;
inline int read(){
	register int x=0,f=0,ch=getchar();
	while('0'>ch||ch>'9')f^=ch=='-',ch=getchar();
	while('0'<=ch&&ch<='9')x=(x<<1)+(x<<3)+(ch^'0'),ch=getchar();
	return f?-x:x;
}
queue<int>q;
const int MAX=3005;
int pos,a,b,n,m;
struct E{
	int e,next;
}e[MAX<<1];
int cnt=1,head[MAX];
inline void add(int u,int v){
	e[cnt]=(E){v,head[u]};head[u]=cnt++;
} 
int dis[MAX][3];
inline void bfs(int S){
	while(!q.empty())q.pop();
	q.push(S);
	dis[S][pos]=0;
	while(!q.empty()){
		a=q.front();q.pop();
		for(register int i=head[a];i;i=e[i].next){
			if(dis[e[i].e][pos]>dis[a][pos]+1){
				dis[e[i].e][pos]=dis[a][pos]+1;
				q.push(e[i].e);
			}
		}	
	}
	++pos;
}
int s,t,s0,t0,ans=0x3f3f3f3f;
signed main(){
	memset(dis,0x3f,sizeof(dis));
	n=read(),m=read();
	for(register int i=1;i<=m;++i){
		a=read(),b=read();
		add(a,b);add(b,a);
	}
	s=read(),t=read(),s0=read(),t0=read();
	bfs(1);bfs(s);bfs(s0);
//	for(register int i=1;i<=n;++i){
//		printf("%d %d %d\n",dis[i][0],dis[i][1],dis[i][2]);
//	}
	for(register int i=1;i<=n;++i){
		if(dis[i][0]+dis[i][1]<=t&&dis[i][0]+dis[i][2]<=t0){
			ans=ans<(dis[i][0]+dis[i][1]+dis[i][2])?ans:(dis[i][0]+dis[i][1]+dis[i][2]);
		}
	}
	if(ans==0x3f3f3f3f)printf("-1\n");
	else printf("%d\n",m-ans);
	return 0;
}


