```cpp
#include <bits/stdc++.h>
using namespace std;
#define ll long long
const ll mod = 1000000007;
ll a [500005],b [500005];
int main () {
	ios::sync_with_stdio (false);
	freopen ("sum.in","r",stdin);
	freopen ("sum.out","w",stdout);
	ll n;cin >> n;
	for (int i = 1;i <= n;++ i) {
		cin >> a [i];
		a [i] %= mod;
	}
	for (int i = 1;i <= n;++ i) {
		cin >> b [i];
		b [i] %= mod;
	}
	//前缀和
	ll last = 0;
	ll ans = 0;
	ll s1 = 0,s2 = 0;
	for (int q = n;q;-- q) {
		s1 += ((n - q) * a [q + 1]) % mod;
		s1 %= mod;
		s2 += ((n - q) * b [q + 1]) % mod;
		s2 %= mod;
		last += (a [q] * b [q]) % mod + ((((n - q) * a [q]) % mod) * b [q]) % mod + (b [q] * s1) % mod + (a [q] * s2) % mod;
		last %= mod;
		ans = (ans + last) % mod; 
	}
	cout << ans << endl;
	return 0;
}
