	for(i=1;i<=n;i++){
		for(j=1;j<=n;j++){
			weii=0;
			while(pow(10,weii)<=where)weii++;
			if(n-j<i){
				while(weii<wei){
					printf("0");
					weii++;
				}
				printf("%d",where);
				where++;
			}
