{
	cin>>n;
	for(int i=1;i<=n;i++)
	cin>>s[i].c>>s[i].m>>s[i].e,s[i].sum+=s[i].c+s[i].m+s[i].e;//求每个人的分数和
	for(int i=1;i<=n;i++)
