#include<bits/stdc++.h>
using namespace std;
#define int long long
int n,a[110];

int _pow(int a, int b, int m) {
    int ans=1;
    a%=m;
    while(b) {
        if(b&1) ans=ans*a%m;
        b/=2;
        a=a*a%m;
    }
    ans%=m;
    return ans;
}

bool MR(int n, int repeat) {
    if(n==2||n==3) return true;
    if(n%2==0||n==1) return false;
    int d=n-1;
    int s=0;
    while(!(d&1)) s++,d>>=1;
    for(int i=1;i<=repeat;i++) {
        int a=rand()%(n-3)+2;
        int x=_pow(a,d,n);
        int y=0;
        for(int j=0;j<s;j++) {
            y=x*x%n;
            if(y==1&&x!=1&&x!=(n-1)) return false;
            x=y;
        }
        if(y!=1) return false;
    }
    return true;
}
#undef int
int main() {
    cin>>n;
    for(int i=1;i<=n;i++) {
        cin>>a[i];
        if(MR(a[i],60)) cout<<a[i]<<" ";
    }
    return 0;
}
