#define maxn 5000010
long long n,m;
long long a[maxn];
int sum,ans,ai,aj;
inline int read()
{
	char c;int sign=1;
	while((c=getchar())<'0'||c>'9') if(c=='-') sign=-1;
	int ret=c-'0';
	while((c=getchar())>='0'&&c<='9') ret=ret*10+c-'0';
	return ret*sign;
}
int main()
{
	cin>>n>>m;
	int i;
	for(i=1;i<=n;i++)
	{
		a[i]=read();

	}
	i=1;
	int j=1;
	while(i<=n)
	{
		while(j<=n&&sum+a[j]<=m)
		{
			sum+=a[j];
			j++;
		}
		if(sum<=m&&sum>ans)
		{
			ans=sum;
			ai=i;
			aj=j-1;
		}
		sum-=a[i];
		i++;
	}
	cout<<ai<<" "<<aj<<" "<<ans;
