	}
};
node t[1000001];
vector<int>s[1000001];
int n,a[1000001],len,d[2000001];
long long c[1000001],S,ans;
int lowbit(int x)
{
	return x&(-x);
}
void add(int x,int y)
{
	for (;x<=n;x+=lowbit(x))
		c[x]+=y;
	return;
}
long long sum(int x)
{
	S=0;
	for (;x>=1;x-=lowbit(x))
		S+=c[x];
	return S;
}
int main()
{
	cin>>n;
	n*=2;
	for (int i=1;i<=n;++i)
	{
		cin>>a[i];
		if (a[i]<0)
			s[-a[i]].push_back(i);
	}
	for (int i=n;i>=1;--i)
	{
		if (a[i]>0)
		{
			t[++len].l=s[a[i]][s[a[i]].size()-1];
			s[a[i]].pop_back();
			t[len].r=i;
		}
	}
	sort(t+1,t+n/2+1);
	for (int i=1;i<=n/2;++i)
	{
		d[i*2-1]=t[i].l;
		d[i*2]=t[i].r;
	}
	for (int i=1;i<=n;++i)
