#include<iostream>
#include<string>

std::string s;

namespace Trie {
int tot, nxt[100003][27];

void insert(const std::string &s) {
	int p = 0;
	for (int c : s) {
		c -= 'A';
		if (!nxt[p][c]) nxt[p][c] = ++tot;
		p = nxt[p][c];
	}
}
} // Trie

int main() {
	std::ios::sync_with_stdio(false);
	std::cin.tie(nullptr);
	while (std::cin >> s)
		Trie::insert(s);
	std::cout << Trie::tot + 1;
	return 0;
}


