最后上代码：

```cpp
// Code By WLS
#include <bits/stdc++.h>
#define itn int
#define rg register
#define psb push_back
#define pii pair<int, int>
#define priq priority_queue<pii>
#define lpriq priority_queue<pii, vector<pii>, greater<pii> >
#define mp make_pair
#define mem(s, x) memset((s), (x), sizeof(s))
const int N = 5e5 + 5;
using namespace std;
inline int read() {
   int s=0, w=1;
   char ch=getchar();
   while(ch<'0'||ch>'9'){if(ch=='-')w=-1;ch=getchar();}
   while(ch>='0'&&ch<='9') s=(s<<3)+(s<<1)+(ch^48),ch=getchar();
   return s*w;
}
struct node {
	int v, w;
};
int n, m, place[10], a[10] = {0, 1, 2, 3, 4, 5};
vector<node> g[N];

int d[N][10];
bool vis[N], zkx[N];
inline void dij(int s) {
	lpriq q;
    memset(vis, 0, sizeof(vis));
    d[place[s]][s] = 0, q.push(mp(0, place[s]));
    while (!q.empty()) {
        int u = q.top().second;
        q.pop();
        if (vis[u]) continue;
        vis[u] = 1;
        for (int i = 0; i < g[u].size(); i++) {
            int v = g[u][i].v;
            if (d[v][s] > d[u][s] + g[u][i].w) {
                d[v][s] = d[u][s] + g[u][i].w;
                q.push(mp(d[v][s], v));
            }
        }
    }
}
int ans;
int main() {
	n = read(), m = read();
	for (int i = 1; i <= 5; i++) place[i] = read();
	place[0] = 1;
	for (int i = 1; i <= m; i++) {
		int u, v, w;
		u = read(), v = read(), w = read();
		g[u].psb((node){v, w});
		g[v].psb((node){u, w});
	}
	memset(d, 0x3f, sizeof(d));
	for (int i = 0; i <= 5; i++) dij(i);
	ans = 0x7f7f7f7f;
	do {
		int dis = d[place[a[1]]][0];
		for (int i = 1; i < 5; i++) {
			dis += d[place[a[i + 1]]][a[i]];
		}
		ans = min(ans, dis);
	} while (next_permutation(a + 1, a + 6));
	cout << ans << endl;
	return 0;
}

```

