#include<algorithm>
#include<cstring>
#include<vector>
using namespace std;
typedef long long ll;
int sq[105][105];
int n, m;
int t;
void mul(int c[][105],int a[][105],int b[][105])
{
	int temp[105][105];
	memset(temp, 0, sizeof temp);
	for (int k = 0; k <= n;k++)
	{
		for (int i = 0; i <= n;i++)
		{
			for (int j = 0; j <=n;j++)
			{
				temp[i][j] = (temp[i][j] + (ll)a[i][k] * b[k][j]) % 2017;
			}
		}
	}
	memcpy(c, temp, sizeof temp);
}//矩阵乘法板子
int main()
{
	scanf("%d%d", &n, &m);
	for (int i = 1; i <= m;i++)
	{
		int a, b;
		scanf("%d%d", &a, &b);
		sq[a][b] = sq[b][a] = 1;//给a,b之间建一条双向边

        //由于这个图的范围并不大，所以只需要用一个邻接矩阵就可以了，也方便了我们直接使用矩阵快速幂
	}
	for (int i = 0;i<=n;i++)
		sq[i][i] = 1;//此处是处理在原地不动的情况
	for (int i = 1;i<=n;i++)
		sq[i][0] = 1;//此处时原地自爆的情况
	scanf("%d", &t);
	int res[105][105];
	for (int i = 0; i <= n;i++)
	{
		res[i][i] = 1;//单位矩阵
	}
	while (t)
	{
		if (t & 1)
			mul(res, res, sq);
		mul(sq, sq, sq);
		t >>= 1;
	}//矩阵快速幂来快速求解
	int ans = 0;
	for (int i = 0; i <= n;i++)
