using namespace std;
//模数 
namespace q_wr {
	inline int read() {
		char c = getchar();
		int x = 0, f = 1;
		for (; !isdigit(c); c = getchar()) {
			if (c == '-') f = -1;
		}
		for (; isdigit(c); c = getchar()) {
			x = (x << 1) + (x << 3) + c - '0';
		}
		return x * f;
	}
	inline void write(int x) {
		if (x < 0) {
			x = ~(x - 1);
			putchar('-');
		}
		if (x > 9)
			write(x / 10);
		putchar(x % 10 + '0');
	}
}

using namespace q_wr;

int n, m, t;

struct matrix {
	int g[200][200];
	matrix () { memset(g, 0, sizeof g); }
	inline matrix operator * (const matrix b) const {
		matrix res;
		for (int i = 0; i <= n; i++) 
			for (int j = 0; j <= n; j++)
				for (int k = 0; k <= n; k++)
					res.g[i][j] = (res.g[i][j] + g[i][k] * b.g[k][j]) % mod;
		return res;
	}//由定义得 
};
//这里定义一下矩阵，用结构体 
matrix ans;

inline matrix mypow (int k, matrix base) {
	matrix res;
	for (int i = 0; i <= n; i++) {
		for (int j = 0; j <= n; j++) {
			res.g[i][j] = base.g[i][j];
		}
	}
	k--;
	while (k) {
		if (k & 1) 
			res = res * base;
		k >>= 1;
		base = base * base;
	}
	return res;
}
//快速幂 
