int insert(int lst,int val,int IDX)
{
	int RT = lst , RTN , RE0;RTN = RE0 = ++ tot;
	for(int i = 30 ; i >= 0 ; i --)
	{
		int id = (val >> i) & 1;
		Trie[RTN][0] = Trie[RT][0] , Trie[RTN][1] = Trie[RT][1];
		cnt[RTN] = cnt[RT] + 1;
		RT = Trie[RT][id];
		Trie[RTN][id] = ++ tot;
		RTN = Trie[RTN][id];
	}
	cnt[RTN] = cnt[RT] + 1;
	idx[RTN] = IDX;
	return RE0;
}
int query(int l,int r,int rk,int L,int R)
{
	rk = (r - l + 1) * (R - L + 1) - rk + 1;
	for(int i = L ; i <= R ; i ++) Linkrt[i][0] = rt[l - 1] , Linkrt[i][1] = rt[r];
	int Cnt = 0,val = 0;
	for(int i = 30 ; i >= 0 ; i --)
	{
		for(int j = L ; j <= R ; j ++) 
		{
			int id = (a[j] >> i) & 1;
			Cnt += cnt[Trie[Linkrt[j][1]][id]] - cnt[Trie[Linkrt[j][0]][id]];	
		}
		if(rk > Cnt) 
		{
			rk -= Cnt , val |= (1 << i);
			for(int j = L ; j <= R ; j ++)
			{
				int id = ((a[j] >> i) & 1) ^ 1;
				Linkrt[j][0] = Trie[Linkrt[j][0]][id] , Linkrt[j][1] = Trie[Linkrt[j][1]][id];
			}
		}
		else
		{
			for(int j = L ; j <= R ; j ++)
			{
				int id = (a[j] >> i) & 1;
				Linkrt[j][0] = Trie[Linkrt[j][0]][id] , Linkrt[j][1] = Trie[Linkrt[j][1]][id];
			}
		}
		Cnt = 0;
	}
	return val;
}
int u,d,l,r,rk;
int main()
{
