		tr[p][c] = ++tot;
		tr[p][!c] = tr[q][!c];
		p = tr[p][c];
		q = tr[q][c];
	}
	siz[p] = siz[q] + 1;
}
int query(int p,int q,int a,int b)//tire树上二分
{
	int res = 0;
	for(int i = 30; i >= 1; i--)
	{
		int A = (a>>i) & 1;
		int B = (b>>i) & 1;
		if(B == 1)
		{
			res += siz[tr[p][A]] - siz[tr[q][A]];
			p = tr[p][!A];
			q = tr[q][!A];
		}
		else 
		{
			p = tr[p][A];
			q = tr[q][A];
		}
	}
	int A = a & 1, B = b & 1;
	if(B == 1) res += siz[tr[p][!A]] - siz[tr[q][!A]];
	res += siz[tr[p][A]] - siz[tr[q][A]];
	return res;
}
bool judge(int mid)
{
	int res = 0;
	for(int i = u; i <= d; i++) res += query(rt[r],rt[l-1],x[i],mid);
	return (d-u+1)*(r-l+1)-res + 1 <= k;
}
