## 贪心
```cpp
#include <cstdio>
#include <algorithm>
#include <queue>
int n;
int c;
int a[64];
std::priority_queue<int, std::vector<int>, std::greater<int> > heap;
int main()
{
    scanf("%d%d", &n, &c);
    for(int i = 1; i <= n; i++)
    {
        scanf("%d", &a[i]);
        heap.push(a[i]);
    }
    int cnt = 0;
    while(c != 0 || heap.top() - cnt != 0)
    {
        int first = heap.top();
        heap.pop();
        int second = heap.top();
        if(second - cnt == 0)
        {
            break;
        }
        int add = std::min(first - cnt + 1, second - first + 1);
        cnt += add;
        if(add > c)
        {
            first += c;
            c = 0;
        }
        else
        {
            c -= add;
            first += add;
        }
        //printf("%d", add);
        heap.push(first);
    }
    printf("%d", cnt);
    return 0;
}

```
