        for (Int R = mid << 1,j = 0; j < limit; j += R)
        {
            for (Int k = 0,w = 1; k < mid; ++ k,w = 1ll * w * Wn % mod)
            {
                int x = a[j + k],y = 1ll * w * a[j + k + mid] % mod;
                a[j + k] = (x + y) % mod,a[j + k + mid] = (x + mod - y) % mod;
            }
