	scanf("%d",&n);
	int tmp1=n%8;//这个代表n是我们发现的规律中的第几个
	int ans=n/8*15;//这个代表n在第几个循环
	int cnt=0;//这个代表第cnt个数（注意，是数，不是Moo）
	for(int i=1; i<=16; i++)
	{
		if(i%3!=0 && i%5!=0) cnt++;
