```cpp
#include<cstdio>
#include<algorithm>

const int N=1e5,M=1e6;

int h[M+10];
int Ans[M+10],ptr;

template<typename T>
inline T read(T& x)
{
	x=0;int f=1;char ch=getchar();
	while(ch>'9'||ch<'0'){if(ch=='-')f=-1;ch=getchar();}
	while('0'<=ch&&ch<='9')x=(x<<3)+(x<<1)+(ch^48),ch=getchar();
	return x*f;
}

template<typename T>
inline void write(T x)
{
	if(x<0)putchar('-'),x=-x;
	if(x>9)write(x/10);
	putchar(x%10+'0');
}

int main()
{
	int n,m,p;
	read(n),read(m),read(p);
	for(int i=1;i<=n;i++)
	{
		int k;
		read(k);
		for(int j=1;j<=k;j++)
		{
			int x;
			read(x);
			h[x]++;
		}
	}
	int ans=0;
	for(int i=1;i<=m;i++)
		if(h[i]==p)Ans[++ptr]=i,ans++;
	printf("%d\n",ans);
	for(int i=1;i<=ans;i++)
		write(Ans[i]),printf(" ");
	printf("\n");
	return 0;
}
```

