	siz[now] = 1;
	for(int i = f[now]; i;i = t[i].next){
		int v = t[i].v, u = t[i].u;
		if(v != fa[u]){
			fa[v] = u;
			deth[v] = deth[u] + 1;
			dfs1(v);
			siz[u] += siz[v];
			if(siz[son[u]] < siz[v]){
				son[u] = v;
			}
		}
	}
	return ;
}

void dfs2(int now){
	dfn[now] = ++id;
	rev[id] = now;
	if(!son[now])return ;
	top[son[now]] = top[now];
	dfs2(son[now]);
	for(int i = f[now]; i;i = t[i].next){
		int v = t[i].v, u = t[i].u;
		if(v != son[u] && v != fa[u]){
			top[v] = v;
			dfs2(v);
		}
	}
	return ;
}

int jump(int x, int k) {
    int temp = deth[x] - k;
    while (deth[top[x]] > temp) x = fa[top[x]];
    temp = deth[x] - temp;
    return rev[dfn[x] - temp];
}

int main(){
//	freopen("hh.txt", "r", stdin);
	int n = read(), q = read(); cin >> S;
	for(int i = 1;i <= n; i++){
		fa[i] = read();
		if(!fa[i]) root = i;
		else add(fa[i], i);
	}
	deth[root] = 1; /*不要忘记初始化哦*/
	dfs1(root);
	top[root] = root;
	dfs2(root);
	ll ltans = 0, ans = 0;  /*不开long long见祖宗*/
	for(int i = 1;i <= q; i++){
		int x = (get(S) ^ ltans) % n + 1;
		int k = (get(S) ^ ltans) % deth[x];
		ltans = jump(x, k);
		ans ^= (ll)i * ltans;
	}
	printf("%lld\n", ans);
	return 0;
}
