inline bool cmp1(Ask a,Ask b){return a.id<b.id;}
inline int Find(int x)
{
	int l=0,r=cnt;
	while(l<=r)
	{
		int mid=(l+r)>>1;
		if(b[mid]<=x)	l=mid+1;
		else	r=mid-1;
	} 
