#include<bits/stdc++.h>
using namespace std;
#define int long long
#define lowbit(x) (x&-x)
const int maxn=17;
const int mod=1e9+7;
int n;
int f[1<<maxn],g[1<<maxn];
int c[maxn][maxn];
signed main()
{
	scanf("%lld",&n);
	for(int i=1;i<=n;i++)
		for(int j=1;j<=n;j++)
			scanf("%lld",&c[i][j]);
	for(int s=0;s<(1<<n);s++)g[s]=1;
	for(int s=0;s<(1<<n);s++)
		for(int i=1;i<=n;i++)
			for(int j=i+1;j<=n;j++)
				if((s&(1<<(i-1)))&&(s&(1<<(j-1))))g[s]=g[s]*(c[i][j]+1)%mod;//+1->不选
	//for(int s=0;s<(1<<n);s++)printf("%d ",g[s]);
	for(int s=0;s<(1<<n);s++)
	{
		f[s]=g[s];
		for(int t=s;t;t=(t-1)&s)
			if((t&lowbit(s))&&t!=s)
				f[s]=(f[s]-f[t]*g[s^t]%mod+mod)%mod;
	}
	//for(int s=0;s<(1<<n);s++)printf("%d ",f[s]);
	printf("%lld",f[(1<<n)-1]);
	return 0;
}
