				{
					q.push(y);
					inq[y]=true;
					cnt[y]++;
					if(cnt[y]>n+1) return false;//返回 false，意味着无解 
				}
			}
		}
	}
	return true;
}
int main()
{
	scanf("%d%d",&n,&m);
	for(int i=1;i<=n;i++) add_edge(0,i,0);//虚拟源点连边 
	for(int i=1;i<=m;i++)
	{
		int x,y,z;
		scanf("%d%d%d",&x,&y,&z);
		add_edge(x,y,-z);
	}
	if(spfa(0))
