本题使用裸 SPFA ，0 号超级点，最短路建图。

用时 145ms 。

```cpp
#include <iostream>
#include <algorithm>
#include <queue>
#include <cstdio>
#include <cstdlib>
#include <cstring>

using namespace std;

const int NMAX = 5005;
const int MMAX = 50005;
const int INF = 0x7f7f7f7f;

struct edge
{
    int from, to, w;
    edge *nxt;
};

// 全局
int n, m;
// 图
edge edges[MMAX], *head[NMAX];
int cnt;
// SPFA
int dis[NMAX], vis[NMAX];
bool inq[NMAX];

inline void addline(int from, int to, int w)
{
    edges[cnt] = (edge){from, to, w, head[from]};
    head[from] = &edges[cnt++];
}

bool SPFA(int begin)
{
    queue<int> que;
    dis[begin] = 0;
    que.push(begin), inq[begin] = true, vis[begin]++;
    while (!que.empty())
    {
        int fnt = que.front();
        que.pop(), inq[fnt] = false;
        for (register edge *i = head[fnt]; i; i = i->nxt)
            if (dis[i->to] > dis[fnt] + i->w)
            {
                dis[i->to] = dis[fnt] + i->w;
                if (!inq[i->to])
                {
                    que.push(i->to), inq[i->to] = true, vis[i->to]++;
                    if (vis[i->to] > n + 1)
                        return false;
                }
            }
    }
    return true;
}

int main()
{
    scanf("%d%d", &n, &m);
    memset(dis, INF, sizeof(dis));
    for (register int i = 1, c1, c2, y; i <= m; i++)
    {
        scanf("%d%d%d", &c1, &c2, &y);
        addline(c2, c1, y);
    }
    for (register int i = 1; i <= n; i++)
        addline(0, i, 0);

    if (!SPFA(0))
        printf("NO");
    else
        for (register int i = 1; i <= n; i++)
            printf("%d ", dis[i]);
    putchar(10);

    system("pause");
    return 0;
}
```


